mapper.select:
SELECT id, text, url, iconCls FROM menutree menu RIGHT JOIN (SELECT DISTINCT rp.perid FROM role_per rp WHERE EXISTS ( SELECT 1 FROM user_role ur WHERE ur.userid=#{userId} AND rp.roleid=ur.roleid )) a ON menu.id=a.perid WHERE menu.type = 1
and menu.parentId = #{pid}
ORDER BY sn

mapper.update:
update menutree
set text = #{text,jdbcType=VARCHAR},
url = #{url,jdbcType=VARCHAR},
parentId = #{parentId,jdbcType=VARCHAR},
updateTime = #{updateTime,jdbcType=DATE}
where id = #{id,jdbcType=VARCHAR}
